use std::collections::HashMap;
pub fn three_sum(nums: Vec<i32>) -> Vec<Vec<i32>> { let mut res =vec![]; for i in 0..nums.len() { for j in i+1..nums.len() { for m in j+1..nums.len() { if nums[i] + nums[j] + nums[m] == 0{ let mut tmp =vec![]; tmp.push( nums[i]); tmp.push(nums[j]); tmp.push(nums[m]); res.push(tmp); } } } } res
}
fn main() { let mut a1 =vec![-1, 0, 1, 2, -1, -4]; println!("{:?}",three_sum(a1));
} æœ€åçš„ç»“æœ è¾“å‡º : [[-1, 0, 1], [-1, 2, -1], [0, 1, -1]] æˆ‘ä»¬å¯ä»¥åœ¨ å¾ªç¯å‰å…ˆå¯¹æ•°ç»„æ’åº,ç„¶åæ¯å±‚éƒ½å»åˆ¤æ–­å‰ä¸€ä¸ªæ•°å­—æ˜¯å¦å’Œå½“å‰ç›¸åŒ ç›¸åŒ å°±è·³è¿‡ã€‚ use std::collections::HashMap;
pub fn three_sum(nums: Vec<i32>) -> Vec<Vec<i32>> { let mut nums = nums; nums.sort(); let mut res =vec![]; let mut flag = None; for i in 0..nums.len() { //å¦‚æœå’Œå‰ä¸€ä¸ªç›¸åŒ å°±è·³è¿‡ if { flag != None && flag == Some(nums[i])}{continue;} flag = Some(nums[i]); let mut flag1 = None; for j in i+1..nums.len() { //å¦‚æœå’Œå‰ä¸€ä¸ªç›¸åŒ å°±è·³è¿‡ if flag1 != None && flag1 == Some(nums[j]) { continue } flag1 = Some(nums[j]); let mut flag2 = None; for m in j+1..nums.len() { //å¦‚æœå’Œå‰ä¸€ä¸ªç›¸åŒ å°±è·³è¿‡ if flag2 != None && flag2 == Some(nums[m]) { continue } flag2 = Some(nums[m]); if nums[i] + nums[j] + nums[m] == 0{ let mut tmp =vec![]; tmp.push( nums[i]); tmp.push(nums[j]); tmp.push(nums[m]); res.push(tmp); } } } } res
}
fn main() { let mut a1 =vec![0, 0, 0, 0]; println!("{:?}",three_sum(a1)); let mut a1 =vec![-1, 0, 1, 2, -1, -4]; println!("{:?}",three_sum(a1)); } äºæ˜¯æˆ‘ä»¬ å¾ˆé«˜å…´çš„å»leetcode æäº¤ç­”æ¡ˆ,ä½†æ˜¯æœç„¶ å®ƒ å°±è¶…æ—¶äº†,æ¯•ç«Ÿç°åœ¨ å¤æ‚åº¦æ˜¯$O^{3}$çº§åˆ«çš„ã€‚

