pub fn three_sum(nums: Vec<i32>) -> Vec<Vec<i32>> { //ä¸ºäº†å¯¹ä»˜ leetcode ç»™çš„ æ¯”å¦‚åƒ [] [0] [1,2] è¿™æ ·çš„æ²¡ç”¨æ•°æ® if nums.len() < 3 { return vec![];} let mut nums = nums; //ä½œä¸‹ æ’åº nums.sort(); let mut res =vec![]; //å®šä¹‰ c for pointer_c in 0..nums.len() -2{ let mut tag1 = None; if pointer_c >0 && nums[pointer_c] == nums[pointer_c -1] { continue; } //å®šä¹‰æŒ‡é’ˆa è¦æ¯” c å¤§ let mut pointer_a= pointer_c + 1; //å®šä¹‰æŒ‡é’ˆ b let mut pointer_b =nums.len() -1; //å·¦è¾¹çš„æŒ‡é’ˆå°äºå³è¾¹çš„æŒ‡é’ˆ while(pointer_a < pointer_b ){ //å¦‚æœ a + b < -c é‚£ä¹ˆ å³ç§»åŠ¨a æŒ‡é’ˆ a + b å˜å¤§ if nums[pointer_a] + nums[pointer_b] < -nums[pointer_c] { pointer_a += 1; //å¦‚æœ a + b > -c é‚£ä¹ˆ å·¦ç§»åŠ¨b æŒ‡é’ˆ a + b å˜å° }else if nums[pointer_a] + nums[pointer_b] > -nums[pointer_c]{ pointer_b -= 1; //å¦‚æœ a + b = -c æ‰¾åˆ°äº† }else if nums[pointer_a] + nums[pointer_b] == -nums[pointer_c]{ if tag1 == Some(nums[pointer_a]) { pointer_a += 1;pointer_b -= 1; continue;} tag1 = Some(nums[pointer_a]); res.push(vec![nums[pointer_c],nums[pointer_a],nums[pointer_b]]); pointer_a += 1; pointer_b -= 1; } } } res }
fn main() { let mut a1 =vec![-2,0,0,2,2]; println!("{:?}",three_sum(a1)); let mut a1 =vec![-1, 0, 1, 2, -1, -4]; println!("{:?}",three_sum(a1)); let mut a1 =vec![0, 0, 0, 0, 0, 0]; println!("{:?}",three_sum(a1));
} ç¬¬ä¸€æ¬¡è¿è¡Œçš„æ—¶å€™ åªè¶…è¶Šäº† 30% å¤š,ä½† ç¬¬äºŒæ¬¡è¿è¡Œåè¶…è¶Šäº† 73% çœŸæ˜¯æä¸æ‡‚,æˆ‘ä»¬ç®—æ³• å·²ç»å†™çš„éå¸¸ç®€æ´äº†,å¦‚æœè¿˜æœ‰ä»€ä¹ˆåœ°æ–¹å¯ä»¥ä¼˜åŒ– æˆ‘æ„Ÿè§‰ é‚£å°±åªèƒ½åœ¨ æ’åºé‚£é‡Œä¼˜åŒ–äº†ã€‚

