pub fn reverse(x: i32) -> i32 { let mut tmp; if(x < 0 ){ tmp = -x; }else{ tmp = x; } let mut tmp1 = 0; while (tmp >= 1){ //è½¬æ¢æˆ i64 å»åˆ¤æ–­æ˜¯å¦æº¢å‡º let mut tmpcalc = tmp1 as i64 + (tmp % 10) as i64 ; //è¿™é‡Œå½“ åˆ†è§£åˆ°æœ€åä¸€ä½æ•°çš„æ—¶å€™ å°±ä¸è¦å†æˆ10äº† if tmp >= 10 { tmpcalc*= 10; } //åˆ¤æ–­ä¸‹ æ˜¯å¦æº¢å‡º if tmpcalc > 2147483648 || tmpcalc < -2147483648 { return 0;} tmp1 = tmpcalc as i32; tmp = tmp / 10; } //åˆ¤æ–­ x æ˜¯æ­£æ•°è¿˜æ˜¯è´Ÿæ•° if x <0{ -tmp1 }else{ tmp1 }
} fn main() { println!("{}",reverse(102)); println!("{}",reverse(9080980)); println!("{}",reverse(90801290));
}

