pub fn find_median_sorted_arrays(nums1: Vec<i32>, nums2: Vec<i32>) -> f64{ //å·æ‡’ å¤„ç† éƒ½æ˜¯ 1ä¸ªçš„æƒ…å†µ let iseve = (nums1.len() + nums2.len()) % 2 == 0; let mut nums1 = nums1; let mut nums2 = nums2; if nums1.len() > nums2.len(){ let tmp = nums1; nums1 = nums2; nums2 = tmp; } // å·æ‡’ è§£å†³æ•°ç»„æœ‰é•¿åº¦ ä¸€ä¸ªæ•°ç»„ æ²¡é•¿åº¦ if nums1.len() == 0 { if iseve { return (nums2[(nums2.len())/2 -1] as f64 + nums2[(nums2.len() + 1)/2 ] as f64 )/ 2.00; }else{ return nums2[(nums2.len() + 1)/2 -1] as f64 } } let k_2 = (nums1.len() + nums2.len() + 1) / 2 -1; //k ä¸ºå°çš„æ•° let mut k_m = nums1.len() -1; let mut k_n = 0; let k = k_2 / 2 ; if nums1.len() + nums2.len() != 2 { if k_m < k{ k_n = k_2 - k_m -1; }else{ k_m = k; k_n = k_2 - k -1 ; } } //äº¤æ¢æ•°ç»„ if nums1[k_m] < nums2[k_n] { let tmp = nums1; nums1 = nums2; nums2 = tmp; let tmp = k_n; k_n = k_m; k_m = tmp; } //è¿™é‡Œ push å‡ ä¸ªå€¼ æ˜¯ä¸ºäº† å¤„ç†è¾¹ç•Œçš„æƒ…å†µ nums1.insert(0,std::i32::MIN); nums2.push(std::i32::MAX); nums1.push(std::i32::MAX); k_m +=1; //ä»¥ä¸‹æ˜¯ æ ¸å¿ƒä»£ç  ä¸Šé¢é¢„å¤„ç† æ²¡ä»€ä¹ˆæŠ€æœ¯å«é‡ while k_n + 1 < nums2.len() -1 && nums1[k_m] > nums2[k_n + 1]{ if k_m == 0 { k_n +=1; }else{ k_m -= 1; k_n +=1; } } if iseve { return (max(nums1[k_m],nums2[k_n]) as f64 +min(nums1[k_m +1],nums2[k_n +1]) as f64 )/ 2.00; }else{ return max(nums1[k_m],nums2[k_n]) as f64; } -1.0 }
fn main() { let m = vec! [1,2] ; let n = vec! [3,4] ; println!("{}",find_median_sorted_arrays(m,n));
