fn shell<T: Ord + Copy>(a: &mut [T]) { let n = a.len(); let mut h = 1; while h < n / 3 { h = h * 3 + 1; } while h >= 1 { for i in h..n { let tmp = a[i]; let mut j = i; while tmp < a[j - h] { a[j] = a[j - h]; j -= h; if let None = j.checked_sub(h) { break; } } a[j] = tmp; } h /= 3; }
}
fn main() { let mut a = vec![1,2,3,0,0,0]; let mut b = vec![2,5,6]; merge(a.as_mut(), 3, b.as_mut(), 3); println!("Hello, world! {:?}",a);
