use std::ops::Add;
impl Solution {
pub fn reverse_words(s: String) -> String { if s == "".to_string(){ return "".to_string() } let mut pointer_a = 0 as usize; let mut pointer_b = s.len() -1; let mut a = s.into_bytes(); while pointer_a < pointer_b { let tmp = a[pointer_a]; a[pointer_a] = a[pointer_b]; a[pointer_b] = tmp; pointer_a +=1; pointer_b -=1; } let mut m:String = a.iter().map(|x| *x as char).collect(); let mut s: Vec<&str> = m.split(" ").collect(); pointer_b = s.len() -1; pointer_a = 0; while pointer_a < pointer_b { let tmp = s[pointer_a]; s[pointer_a] = s[pointer_b]; s[pointer_b] = tmp; pointer_a +=1; pointer_b -=1; } s.join(" ") }
} 时间复杂度：O(n) 空间复杂度：O(1)

