pub fn reverse(x: i32) -> i32 { let mut tmp; if(x < 0 ){ tmp = -x; }else{ tmp = x; } let mut tmp1 = 0; while (tmp >= 1){ //转换成 i64 去判断是否溢出 let mut tmpcalc = tmp1 as i64 + (tmp % 10) as i64 ; //这里当 分解到最后一位数的时候 就不要再成10了 if tmp >= 10 { tmpcalc*= 10; } //判断下 是否溢出 if tmpcalc > 2147483648 || tmpcalc < -2147483648 { return 0;} tmp1 = tmpcalc as i32; tmp = tmp / 10; } //判断 x 是正数还是负数 if x <0{ -tmp1 }else{ tmp1 }
} fn main() { println!("{}",reverse(102)); println!("{}",reverse(9080980)); println!("{}",reverse(90801290));
}

