macro_rules! pop { ($name:expr) => { $name.pop().unwrap() };
}
pub fn eval_rpn(tokens:Vec<String>) -> i32 { let mut stack:Vec<i32> = vec![]; for i in 0..tokens.len(){ let token = tokens[i].clone(); match token { _ if token == "+" => { let first = pop!(stack); let second = pop!(stack); stack.push(second + first); }, _ if token == "-" => { let first = pop!(stack); let second = pop!(stack); stack.push(second - first); }, _ if token == "*" => { let first = pop!(stack); let second = pop!(stack); stack.push(second * first); }, _ if token == "/" => { let first = pop!(stack); let second = pop!(stack); stack.push(second/ first); }, //如果是一个number 压栈 _ => { stack.push(token.parse::<i32>().unwrap()) } } } pop!(stack) } fn main() { let mut m:Vec<String> =Vec::new(); m.push("2".parse().unwrap()); m.push("1".parse().unwrap()); m.push("+".parse().unwrap()); m.push("3".parse().unwrap()); m.push("*".parse().unwrap()); let len = m.len()-1; println!("{:?}",m); let res = eval_rpn(m); println!("{}",res);
} 时间复杂度：O (n) 空间复杂度：O (n)

